import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import Quiz from "./components/Quiz";
import Instructions from "./components/Instructions";
import Signin from "./components/Signin";
import CreateTest from "./components/CreateTest";

const App = () => {
  const [flag, setFlag] = useState(() => {
    const savedFlag = sessionStorage.getItem("flag");
    return savedFlag !== null ? JSON.parse(savedFlag) : 0;
  });

  const initialTime = 100; // Time for the quiz (in minutes)

  // Example questions data
  const questions = [
    {
      question: "Which of the following are fruits?",
      type: "checkbox",
      options: ["Apple", "Carrot", "Banana", "Broccoli"],
    },
    {
      question: "What is the capital of France?",
      type: "radio",
      options: ["Berlin", "Madrid", "Paris", "Rome"],
    },
    // Add more questions here
  ];

  const instructions = [
    "Instruction 1: Please read carefully.",
    "Instruction 2: Choose the correct answers.",
    "Instruction 3: Time limit is 10 minutes.",
  ];

  const profile = {
    Name: "SK",
    Department: "B.E Computer Science And Engineering",
    "Roll Number": "310821104083",
  };

  useEffect(() => {
    sessionStorage.setItem("flag", JSON.stringify(flag));
  }, [flag]);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Signin setFlag={setFlag} />} />
        <Route
          path="/instructions"
          element={
            <Instructions
              instructions={instructions}
              profile1={profile}
              setFlag={setFlag}
            />
          }
        />
        <Route
          path="/quiz"
          element={
            <Quiz
              setFlag={setFlag}
              initialTime={initialTime}
              profile1={profile}
              questions={questions}
            />
          }
        />
        <Route
          path="/createtest"
          element={
            <CreateTest
              setFlag={setFlag}
              initialTime={initialTime}
              profile1={profile}
              questions={questions}
            />
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
