import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

import Quiz from "./pages/Quiz";
import Instructions from "./pages/Instructions";
import Signin from "./pages/Signin";
import Admin from "./pages/Admin";
import Students from "./pages/Students";

const App = () => {
  const fetchData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/data`);

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const result = await response.json();

      sessionStorage.setItem("localIps", result);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  fetchData();

  const initialTime = 100;
  // Example questions data
  const questions = [
    {
      question: "Which of the following are fruits?",
      type: "checkbox",
      options: ["Apple", "Carrot", "Banana", "Broccoli"],
    },
    {
      question: "What is the capital of France?",
      type: "radio",
      options: ["Berlin", "Madrid", "Paris", "Rome"],
    },
    // Add more questions here
  ];

  const instructions = [
    "Instruction 1: Please read carefully.",
    "Instruction 2: Choose the correct answers.",
    "Instruction 3: Time limit is 10 minutes.",
  ];

  const profile = {
    Name: "SK",
    Department: "B.E Computer Science And Engineering",
    "Roll Number": "310821104083",
  };

  return (
    <Router>
      <Routes>
        <Route
          index
          element={
            <>
              <ChangeTitle title="Home" />
              <Signin />
            </>
          }
        />
        <Route
          path="/instructions"
          element={
            <>
              <ChangeTitle title="Instructions" />
              <Instructions instructions={instructions} profile1={profile} />
            </>
          }
        />
        <Route
          path="/quiz"
          element={
            <>
              <ChangeTitle title="Quiz" />
              <Quiz
                initialTime={initialTime}
                profile1={profile}
                questions={questions}
              />
            </>
          }
        />
        <Route
          path="/admin"
          element={
            <>
              <ChangeTitle title="Admin" />
              <Admin initialTime={initialTime} profile1={profile} />
            </>
          }
        />
        <Route
          path="/students-module"
          element={
            <>
              <ChangeTitle title="Students Module" />
              <Students profile1={profile} />
            </>
          }
        />
      </Routes>
    </Router>
  );
};
const ChangeTitle = ({ title }) => {
  useEffect(() => {
    document.title = title;
  }, [title]);
  return null;
};

export default App;
