import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Students.css";
import DataTable from "react-data-table-component";

import components from "./components";
const { Navbar, handleApiCall, Modal } = components;

const Students = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalOptions, setModalOptions] = useState();

  const [hosts, sd] = useState(false);
  const [tableColumns, setTableColumns] = useState([]);
  const [tableData, setTableData] = useState([]);

  const [isSelectable, setIsSelectable] = useState(false);

  const [selectedRows, setSelectedRows] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await handleApiCall({
          API: "load-data",
          data: { collection: "Users" },
        });

        if (response.flag) {
          response.data.map((value, index) => {
            delete value["_id"];
          });
          setTableColumns(Object.keys(response.data[0]).slice(1));
          setTableData(response.data);
        } else {
          setModalOptions({
            type: "Error",
            message: "Error loading data",
            buttons: ["Retry", "Ok"],
            responseFunc: (button) => {
              if (button === "Retry") {
                fetchData();
                console.log("retyr");
              } else {
                setIsModalOpen(false);
              }
            },
          });
          setIsModalOpen(true);
        }
      } catch (error) {
        console.error("Login error:", error);
      }
    }
    fetchData();
  }, []);

  const fileUpload = (loadData) => {
    let file = document.querySelector("#students-list");
    if (!file.files[0]) return;

    const reader = new FileReader();
    reader.onload = () => {
      axios
        .post(`http://${hosts[0]}:5000/Upload-data`, { data: reader.result })
        .then(() => {
          window.alert("Uploaded to primary server");
          file.value = null;
          loadData();
        })
        .catch(() => {
          if (hosts[1]) {
            axios
              .post(`http://${hosts[1]}:5000/Upload-file`, {
                data: reader.result,
              })
              .then(() => {
                console.log("Uploaded to secondary server");
                loadData();
              })
              .catch((error) => {
                console.error("Error uploading to secondary server:", error);
              });
          }
        });
    };

    reader.readAsText(file.files[0]);
  };

  const addNew = () => {
    setIsModalOpen(true);

    const overlay = document.createElement("div");
    overlay.className = "overlay";
    document.body.appendChild(overlay);

    const modalContainer = document.createElement("div");
    modalContainer.className = "modal-container";

    tableColumns.forEach((column) => {
      const inputField = document.createElement("input");
      inputField.className = column;
      inputField.placeholder = column;
      modalContainer.appendChild(inputField);
    });

    const saveButton = document.createElement("button");
    saveButton.innerText = "Save";
    saveButton.onclick = () => {
      let data = {};
      tableColumns.forEach((column) => {
        const val = document.getElementsByClassName(column)[0].value;
        data[column] = val;
      });

      axios
        .post(`http://${hosts[0]}:5000/insert-data`, {
          data,
          collection: "Users",
        })
        .then(() => {
          setTableData([...tableData, data]);
          window.alert("New user added successfully!");
        })
        .catch((error) => {
          console.error(error);
        });

      modalContainer.remove();
      document.body.removeChild(overlay);
      closeModal();
    };

    const closeButton = document.createElement("button");
    closeButton.innerText = "Close";
    closeButton.onclick = () => {
      closeModal();
      modalContainer.remove();
      document.body.removeChild(overlay);
    };

    modalContainer.appendChild(saveButton);
    modalContainer.appendChild(closeButton);
    document.body.appendChild(modalContainer);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const remove = () => {
    if (!isSelectable) {
      setIsSelectable(true);
    } else {
      if (selectedRows.length > 0) {
        const filteredData = tableData.filter(
          (row) => !selectedRows.includes(row)
        );

        setTableData(filteredData);

        axios
          .post(`http://${hosts[0]}:5000/delete-data`, {
            collection: "Users",
            data: selectedRows.map((row) => row.Contact), // Sending the _id values
          })
          .then((result) => {
            window.alert(`${result.data.deletedCount} Deleted Successfully!`);
          })
          .catch((error) => {
            console.error("Error deleting documents:", error);
          });

        setSelectedRows([]);
      }
      setIsSelectable(false);
    }
  };

  const handleRowSelected = (state) => {
    //console.log(selectedRows);
    setSelectedRows(state.selectedRows);
  };

  const columns = [
    {
      name: "S.No",
      selector: (row, index) => index + 1,
      sortable: true,
      width: "70px",
    },
    ...tableColumns.map((column) => ({
      name: column,
      selector: (row) => row[column],
      sortable: true,
    })),
  ];

  const data = tableData;

  const customStyles = {
    rows: {
      style: {
        maxHeight: "72px",
      },
    },
    headCells: {
      style: {
        color: "white",
        fontSize: "larger",
        fontWeight: "bold",
        backgroundColor: "#007bff",
        paddingLeft: "8px",
        paddingRight: "8px",
      },
    },
    cells: {
      style: {
        paddingLeft: "8px",
        paddingRight: "8px",
      },
    },
  };

  return (
    <>
      <Navbar />
      <div className="students-action-div">
        <div
          style={{
            display: "inline-flex",
            textAlign: "start",
            flexDirection: "column",
          }}
        >
          <label style={{ marginLeft: "5px", marginBottom: "5px" }}>
            Upload Students List
          </label>
          <input name="student-list" id="students-list" type="file" required />
        </div>
        <div>
          <button
            type="button"
            //onClick={() => fileUpload(loadData)}
            className="upload-button"
          >
            Upload
          </button>
          <button type="button" onClick={addNew} className="upload-button">
            Add New
          </button>
          <button type="button" onClick={remove} className="upload-button">
            Remove
          </button>
        </div>
      </div>
      <div className="datatable">
        <DataTable
          columns={columns}
          data={data}
          highlightOnHover
          striped
          fixedHeaderScrollHeight="80vh"
          defaultSortFieldId={1}
          customStyles={customStyles}
          responsive
          fixedHeader
          selectableRows={isSelectable} // Conditional selection
          onSelectedRowsChange={handleRowSelected}
        />
      </div>
      {isModalOpen && (
        <Modal
          modalType={modalOptions.type}
          modalMessage={modalOptions.message}
          buttons={modalOptions.buttons}
          response={modalOptions.responseFunc}
        />
      )}
    </>
  );
};

export default Students;
