import "../styles/Students.css";
import { useState } from "react";

import components from "./components";
const { DataTableManagement } = components;

const Students = () => {
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    setLoading(true);
    try {
      await DataTableManagement.fileUpload(DataTableManagement.fetchData);
    } catch (error) {
      console.error("Upload failed:", error);
      // Handle error accordingly (e.g., show a notification)
    } finally {
      setLoading(false);
    }
  };

  const handleAddNew = async () => {
    setLoading(true);
    try {
      await DataTableManagement.addNew();
    } catch (error) {
      console.error("Add new failed:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleRemove = async () => {
    setLoading(true);
    try {
      await DataTableManagement.remove();
    } catch (error) {
      console.error("Remove failed:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <DataTableManagement
        tablePageName={"Students"}
        collectionName={"Users"}
        actionButtons={{
          Upload: {
            onClick: handleUpload,
            type: "button",
            className: "upload-button",
            disabled: loading,
          },
          "Add New": {
            onClick: handleAddNew,
            type: "button",
            className: "upload-button",
            disabled: loading,
          },
          Remove: {
            onClick: handleRemove,
            type: "button",
            className: "upload-button",
            disabled: loading,
          },
        }}
      />
      {loading && <p>Loading...</p>}
    </>
  );
};

export default Students;
