// src/components/Quiz.js

import React, { useState } from 'react';
import '/styles/Quiz.css'; // Import the CSS file

const Quiz = () => {
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [result, setResult] = useState('');

  const questions = [
    {
      question: 'What is the capital of France?',
      options: ['Berlin', 'Madrid', 'Paris', 'Rome'],
      answer: 'Paris',
    },
    {
      question: 'Which planet is known as the Red Planet?',
      options: ['Earth', 'Mars', 'Jupiter', 'Saturn'],
      answer: 'Mars',
    },
    {
      question: 'Who wrote "To Kill a Mockingbird"?',
      options: ['Harper Lee', 'J.K. Rowling', 'Mark Twain', 'Ernest Hemingway'],
      answer: 'Harper Lee',
    },
  ];

  const handleAnswerSelect = (option) => {
    setSelectedAnswer(option);
    const currentQuestion = questions[0]; // Only the first question for simplicity
    if (option === currentQuestion.answer) {
      setResult('Correct!');
    } else {
      setResult('Wrong answer. Try again!');
    }
  };

  return (
    <div className="quiz-container">
      <h1>Quiz</h1>
      {questions.map((q, index) => (
        <div key={index}>
          <h3>{q.question}</h3>
          {q.options.map((option) => (
            <button
              key={option}
              onClick={() => handleAnswerSelect(option)}
              disabled={selectedAnswer !== null}
            >
              {option}
            </button>
          ))}
          {selectedAnswer && <p>{result}</p>}
        </div>
      ))}
    </div>
  );
};

export default Quiz;
