import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Signin.css";
import components from "./components";
const { handleApiCall, Modal, Navbar, Response } = components;

const Signin = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [responseMessage, setResponseMessage] = useState(null);
  const [userID, setUserID] = useState("");
  const [userPassword, setUserPassword] = useState("");

  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!userID || !userPassword) {
      if (userID.trim() === "" && userPassword.trim() === "") {
        setResponseMessage("Please Enter UserId & Password");
      } else if (userID.trim() === "") {
        setResponseMessage("Please Enter UserId");
      } else if (userPassword.trim() === "") {
        setResponseMessage("Please Enter Password");
      }
    } else {
      const response = await handleApiCall({
        API: "login",
        data: { Id: userID, userPass: userPassword },
      });

      if (response && response.flag) {
        localStorage.setItem("userData", JSON.stringify(response.data));
        setIsModalOpen(true);
      } else {
        setResponseMessage(response.error);
      }
    }
  };

  const closeModal = (button) => {
    if (Response(["Ok"], button)) {
      navigate("/instructions");
      setIsModalOpen(false);
    }
  };

  return (
    <>
      <Navbar page={true} />

      <div className="login-container">
        <h1 className="login-header">Login</h1>
        <div className="login-form">
          <div className="institution-name">
            <h1>Jeppiaar University</h1>
          </div>
          <div className="input-group">
            <label htmlFor="user-id">User ID</label>
            <input
              name="user-id"
              type="text"
              value={userID}
              required
              onChange={(e) => {
                setUserID(e.target.value);
              }}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              name="password"
              type="password"
              value={userPassword}
              required
              onChange={(e) => {
                setUserPassword(e.target.value);
              }}
            />
          </div>
          {responseMessage && (
            <div className="error-message" style={{ color: "red" }}>
              {responseMessage}
            </div>
          )}

          <button onClick={handleLogin} type="button">
            Login
          </button>
        </div>
      </div>

      {isModalOpen && (
        <Modal
          modalType="Info"
          modalMessage="Login Successful!"
          buttons={["Ok"]}
          response={closeModal}
        />
      )}
    </>
  );
};

export default Signin;
