import React, { useState, useEffect } from "react";
import "../styles/Quiz.css";

const Quiz = ({ initialTime, questions = [] }) => {
  const [timeLeft, setTimeLeft] = useState(initialTime); // Initial time from DB
  const [totalTime, setTotalTime] = useState(initialTime); // Also store total time
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Index to track the current question
  const [selectedOptions, setSelectedOptions] = useState([]); // Store selected options for each question
  const [highlightedOption, setHighlightedOption] = useState(null); // Store highlighted option for the current question

  const totalQuestions = questions.length; // Total number of questions

  // Timer logic, decrements timeLeft every second
  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer); // Clear the timer on component unmount
    }
  }, [timeLeft]);

// Handle option selection
const handleOptionSelect = (option) => {
    const currentQuestion = questions[currentQuestionIndex];

    if (currentQuestion.type === "radio") {
      // For radio buttons, only one option is allowed
      setHighlightedOption(option); // Highlight the selected option
    } else if (currentQuestion.type === "checkbox") {
      // For checkboxes, toggle the highlighting of the option
      const currentSelections = selectedOptions[currentQuestionIndex] || [];

      if (currentSelections.includes(option)) {
        // If already selected, we may want to reset the highlight
        setHighlightedOption(null); // Remove highlight if already selected
      } else {
        // Highlight the newly added option
        setHighlightedOption(option); // Highlight only if the current option is newly selected
      }
    }
  };




  // Go to the next question
  const handleNextQuestion = () => {
    if (currentQuestionIndex < totalQuestions - 1) {
      const updatedSelections = [...selectedOptions];
      updatedSelections[currentQuestionIndex] = highlightedOption; // Save highlighted option to selected options
      setSelectedOptions(updatedSelections);
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1); // Go to the next question
      setHighlightedOption(null); // Clear highlighted option
    }
  };

  // Go to the previous question
  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      const updatedSelections = [...selectedOptions];
      updatedSelections[currentQuestionIndex] = highlightedOption; // Save highlighted option to selected options
      setSelectedOptions(updatedSelections);
      setCurrentQuestionIndex((prevIndex) => prevIndex - 1); // Go to the previous question
      setHighlightedOption(null); // Clear highlighted option
    }
  };

  // Handle quiz submission
  const handleSubmit = () => {
    console.log("Quiz Submitted:", selectedOptions);
    alert("Quiz Submitted!");
  };

  const currentQuestion = questions[currentQuestionIndex];
  const selectedOption = selectedOptions[currentQuestionIndex] || [];

  const radius = 50; // Radius of the circle
  const circumference = 2 * Math.PI * radius; // Circumference of the circle

  return (
    <>
      {/* Top Navigation Bar */}
      <div>
        <nav className="navbar">
          <div className="logo">Company Name</div>
          <div className="nav-links">
            <a href="#home">Home</a>
            <a href="#about">About</a>
            <a href="#contact">Contact</a>
          </div>
        </nav>
      </div>
      {/* Main Content Area */}
      <div className="quiz-body">
        <div className="quiz-app">
          <div className="quiz-content">
            <div className="question-section">
              <h1>{currentQuestion?.question}</h1>
              <ul className="options">
  {currentQuestion?.options.map((option, index) => (
    <li
      key={index}
      onClick={() => handleOptionSelect(option)}
      className={`option ${
        highlightedOption === option ? "selected" : ""
      }`}
    >
      <label>
        <input
          type={currentQuestion.type}
          name={`question-${currentQuestionIndex}`}
          value={option}
          checked={selectedOption === option}
          readOnly // Prevent checking directly on input
        />
        {option}
      </label>
    </li>
  ))}
</ul>

            </div>
          </div>

          {/* Navigation and Footer Section */}
          <div className="footer">
            <button
              onClick={handlePreviousQuestion}
              disabled={currentQuestionIndex === 0}
            >
              Previous
            </button>
            {currentQuestionIndex < totalQuestions - 1 ? (
              <button onClick={handleNextQuestion}>Next</button>
            ) : (
              <button onClick={handleSubmit}>Submit</button>
            )}
            <p>
              Question {currentQuestionIndex + 1} of {totalQuestions}
            </p>
          </div>
        </div>
        <div>
          {/* Timer Section */}
          <div className="timer-section">
            <div className="timer-circle">
              <svg width="120" height="120">
                <circle
                  r={radius}
                  cx="60"
                  cy="60"
                  fill="none"
                  stroke="#e7e7e7"
                  strokeWidth="8"
                ></circle>
                <circle
                  r={radius}
                  cx="60"
                  cy="60"
                  fill="none"
                  stroke="#007BFF"
                  strokeWidth="8"
                  strokeDasharray={circumference}
                  strokeDashoffset={
                    circumference - (timeLeft / totalTime) * circumference
                  } // Dynamic time calculation
                  style={{
                    transition: "stroke-dashoffset 1s linear",
                  }}
                ></circle>
              </svg>
              <div className="timer-text">
                {Math.floor(timeLeft / 60)}m {timeLeft % 60}s
              </div>
            </div>
            <div>Time Remaining</div>
          </div>
          {/* Question Number Section */}
          <div className="question-number-container">
            <h1>Questions List</h1>
            <div className="grid-layout">
              {Array.from({ length: totalQuestions }, (_, i) => (
                <div
                  className={`question-number ${
                    selectedOptions[i] != undefined ? "selected" : ""
                  }`}
                  key={i + 1}
                >
                  {i + 1}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Quiz;
