// src/components/Quiz.js

import React, { useState } from "react";
import "../styles/Quiz.css"; // Import the CSS file

const Quiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [result, setResult] = useState("");

  const questions = [
    {
      question: "What is the capital of France?",
      options: ["Berlin", "Madrid", "Paris"],
      answer: "Paris",
    },
    {
      question: "Which planet is known as the Red Planet?",
      options: ["Earth", "Mars", "Jupiter"],
      answer: "Mars",
    },
    {
      question: 'Who wrote "To Kill a Mockingbird"?',
      options: ["Harper Lee", "J.K. Rowling", "Mark Twain"],
      answer: "Harper Lee",
    },
  ];

  const handleAnswerSelect = (option) => {
    setSelectedAnswer(option);
    const currentQuestion = questions[currentQuestionIndex];
    if (option === currentQuestion.answer) {
      setResult("Correct!");
    } else {
      setResult("Wrong answer. Try again!");
    }
  };

  const handleNextQuestion = () => {
    setSelectedAnswer(null); // Reset the selected answer for the next question
    setResult(""); // Reset the result message
    setCurrentQuestionIndex((prevIndex) =>
      Math.min(prevIndex + 1, questions.length - 1)
    );
  };

  const handlePreviousQuestion = () => {
    setSelectedAnswer(null); // Reset the selected answer for the previous question
    setResult(""); // Reset the result message
    setCurrentQuestionIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <>
      <div className="header">
        <h1>Jeppiaar University</h1>
      </div>
      <div Q>
      <div className="quiz-container">
        <h1>Quiz</h1>
        <div>
          <h3>
            {currentQuestionIndex + 1}. {currentQuestion.question}
          </h3>
          {currentQuestion.options.map((option, index) => (
            <div key={option}>
              <input
                type="radio"
                id={`option-${index}`} // Unique ID for each radio button
                name="quiz-options" // Grouping for radio buttons
                value={option}
                onChange={() => handleAnswerSelect(option)}
                checked={selectedAnswer === option} // Check the selected answer
                disabled={selectedAnswer !== null} // Disable after answering
              />
              <label htmlFor={`option-${index}`}>
                <span id="option">{String.fromCharCode(65 + index)}.</span>{" "}
                {option} {/* A, B, C labels */}
              </label>
            </div>
          ))}
          {selectedAnswer && <p>{result}</p>}
        </div>

        <div className="navigation-buttons">
          {/* Conditionally render the Previous button */}
          {currentQuestionIndex > 0 && (
            <button onClick={handlePreviousQuestion}>Previous</button>
          )}
          {currentQuestionIndex < questions.length - 1 && (
            <button onClick={handleNextQuestion}>Next</button>
          )}
        </div>
      </div>
      <div className="timerContainer">
        <div>Total Time:{100}</div>
        <div>Time Taken:{200}</div>
      </div></div>
    </>
  );
};

export default Quiz;
