import React, { useState, useEffect } from "react";
import Quiz from "./components/Quiz";
import Instructions from "./components/Instructions";
import Signin from "./components/Signin";

const App = () => {
  // Retrieve the flag from localStorage or default to 0
  const [flag, setFlag] = useState(() => {
    const savedFlag = sessionStorage.getItem("flag");
    return savedFlag !== null ? JSON.parse(savedFlag) : 0;
  });

  const initialTime = 100; // Time for the quiz (in minutes)

  // Example questions data
  const questions = [
    {
      question: "Which of the following are fruits?",
      type: "checkbox",
      options: ["Apple", "Carrot", "Banana", "Broccoli"],
    },
    {
      question: "What is the capital of France?",
      type: "radio",
      options: ["Berlin", "Madrid", "Paris", "Rome"],
    },
    {
      question: "Which programming languages do you know?",
      type: "checkbox",
      options: ["Python", "JavaScript", "C++", "Ruby"],
    },
    {
      question: "What is the largest planet in our solar system?",
      type: "radio",
      options: ["Earth", "Mars", "Jupiter", "Venus"],
    },
    {
      question: "What is the largest planet in our solar system?",
      type: "radio",
      options: ["Earth", "Mars", "Jupiter", "Venus"],
    },
  ];

  const instructions = [
    "Instruction 1: Please read carefully.",
    "Instruction 2: Choose the correct answers.",
    "Instruction 3: Time limit is 10 minutes.",
    "Instruction 4: Once you start the quiz, no going back.",
    "Instruction 5: Submit your answers at the end.",
  ];
  const profile = {
    Name: "SK",
    Department: "B.E Computer Science And Engineering",
    "Roll Number": "310821104083",
  };
  // Whenever `flag` changes, save it to localStorage
  useEffect(() => {
    sessionStorage.setItem("flag", JSON.stringify(flag));
  }, [flag]);

  return (
    <>
      {flag === 0 ? (
        <div className="App">
          <Signin setFlag={setFlag} />
        </div>
      ) : flag === 1 ? (
        <div className="App">
          <Instructions
            instructions={instructions}
            profile1={profile}
            setFlag={setFlag}
          />
        </div>
      ) : flag === 2 ? (
        <div className="App">
          <Quiz
            setFlag={setFlag}
            initialTime={initialTime}
            profile1={profile}
            questions={questions}
          />
        </div>
      ) : (
        ""
      )}
    </>
  );
};

export default App;
