import React, { useState, useEffect } from "react";
import { CgProfile } from "react-icons/cg";
import "../styles/Quiz.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Quiz = ({ initialTime }) => {
  const [userData, setUserData] = useState();
  const navigate = useNavigate();
  const [timeLeft, setTimeLeft] = useState(initialTime);
  const [totalTime] = useState(initialTime);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [highlightedOptions, setHighlightedOptions] = useState([]);
  const [hosts, setHosts] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("userData"));
    setUserData(userData);
    const localIps = localStorage.getItem("localIps");

    if (localIps) {
      setHosts(JSON.parse(localIps.split(",")));
    }

    const loadQuestions = async () => {
      if (hosts.length > 0) {
        try {
          const result = await axios.post(`http://${hosts[0]}:5000/load-data`, {
            collection: "Tests",
          });
          const cleanedData = result.data.map((qn) => {
            delete qn["_id"];
            return qn;
          });
          setQuestions(cleanedData);
        } catch (error) {
          console.error("Error loading questions:", error);
        }
      }
    };

    loadQuestions();

    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      handleSubmit(); // Automatically submit the quiz when time runs out
    }
  }, [hosts, timeLeft]);

  const totalQuestions = questions.length;

  const handleOptionSelect = (option, type) => {
    const updatedSelections = [...highlightedOptions];

    if (type === "radio") {
      updatedSelections[currentQuestionIndex] =
        option !== undefined ? option : "not-answered";
    } else if (type === "checkbox") {
      const currentSelections = updatedSelections[currentQuestionIndex] || [];
      if (currentSelections.includes(option)) {
        updatedSelections[currentQuestionIndex] = currentSelections.filter(
          (o) => o !== option
        );
      } else {
        updatedSelections[currentQuestionIndex] = [
          ...currentSelections,
          option,
        ];
      }

      if (updatedSelections[currentQuestionIndex].length === 0) {
        updatedSelections[currentQuestionIndex] = "not-answered";
      }
    }

    setHighlightedOptions(updatedSelections);
    setSelectedOptions(updatedSelections);
  };

  const handleNextQuestion = () => {
    if (highlightedOptions[currentQuestionIndex] === undefined) {
      highlightedOptions[currentQuestionIndex] = "not-answered";
    }

    setCurrentQuestionIndex((prevIndex) =>
      Math.min(prevIndex + 1, totalQuestions - 1)
    );
  };

  const handlePreviousQuestion = () => {
    if (highlightedOptions[currentQuestionIndex] === undefined) {
      highlightedOptions[currentQuestionIndex] = "not-answered";
    }

    setCurrentQuestionIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleSkip = () => {
    highlightedOptions[currentQuestionIndex] = "skipped";
    setHighlightedOptions([...highlightedOptions]);
    handleNextQuestion();
  };

  const handleQuestionClick = (index) => {
    if (highlightedOptions[index] === undefined) {
      highlightedOptions[index] = "not-answered";
    }
    setHighlightedOptions([...highlightedOptions]);
    setCurrentQuestionIndex(index);
  };

  const handleSubmit = () => {
    if (window.confirm("Are you sure you want to start the test?")) {
      console.log("Quiz Submitted:", selectedOptions);
      alert("Quiz Submitted!");
      navigate("/");
    }
  };

  const showProfile = (profileDetails) => {
    const profileContainer = document.createElement("div");
    profileContainer.className = "profile-container";
    const profileInfo = document.createElement("div");
    profileInfo.className = "profile-info";

    Object.keys(profileDetails).forEach((detail) => {
      const detailList = document.createElement("li");
      detailList.className = "detail";
      detailList.innerHTML = `<p><span>${detail}:</span>&nbsp;<span>${profileDetails[detail]}</span></p>`;
      profileInfo.appendChild(detailList);
    });

    profileContainer.appendChild(profileInfo);
    document.body.appendChild(profileContainer);

    document.body.addEventListener("click", (event) => {
      if (!event.target.closest(".profile-container")) {
        profileContainer.remove();
      }
    });
  };

  const currentQuestion = questions[currentQuestionIndex];
  const selectedOption = highlightedOptions[currentQuestionIndex] || [];

  const radius = 50;
  const circumference = 2 * Math.PI * radius;

  return (
    <>
      <div>
        <nav className="navbar">
          <div className="logo">Quizzards</div>
          <div className="nav-links">
            <a href="#" onClick={() => navigate("/admin")}>
              Home
            </a>
            <a href="#about">About</a>
            <a
              target="_blank"
              rel="noopener noreferrer"
              href="https://sanjaikumaran.online/contact/"
            >
              Contact
            </a>
            <li onClick={() => showProfile(userData)} className="profile">
              <CgProfile style={{ fontSize: "1.5rem" }} />
            </li>
          </div>
        </nav>
      </div>

      <div className="quiz-body">
        <div className="quiz-app">
          <div className="quiz-content">
            <div className="question-section">
              <h1>{currentQuestion?.question}</h1>
              <ul className="options">
                {currentQuestion?.Option.map((option, index) => (
                  <li
                    key={index}
                    onClick={() =>
                      handleOptionSelect(option, currentQuestion.type)
                    }
                    className={`
                      option 
                      ${
                        currentQuestion.type === "radio" &&
                        selectedOption === option
                          ? "selected"
                          : ""
                      }
                      ${
                        currentQuestion.type === "checkbox" &&
                        selectedOption.includes(option)
                          ? "selected"
                          : ""
                      }
                    `}
                  >
                    {currentQuestion.type === "radio" ? (
                      <label>
                        <input
                          type="radio"
                          name={`question-${currentQuestionIndex}`}
                          value={option}
                          checked={selectedOption === option}
                          onChange={() => handleOptionSelect(option, "radio")}
                        />
                        {option}
                      </label>
                    ) : (
                      <label>
                        <input
                          type="checkbox"
                          name={`question-${currentQuestionIndex}`}
                          value={option}
                          checked={selectedOption.includes(option)}
                          onChange={() =>
                            handleOptionSelect(option, "checkbox")
                          }
                        />
                        {option}
                      </label>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <div className="footer">
            {currentQuestionIndex > 0 && (
              <button onClick={handlePreviousQuestion}>Previous</button>
            )}
            <button
              onClick={
                currentQuestionIndex < totalQuestions - 1
                  ? handleNextQuestion
                  : handleSubmit
              }
              className={
                currentQuestionIndex === totalQuestions - 1
                  ? "submit-button"
                  : ""
              }
            >
              {currentQuestionIndex < totalQuestions - 1 ? "Next" : "Submit"}
            </button>
            {currentQuestionIndex < totalQuestions - 1 && (
              <button onClick={handleSkip}>Skip</button>
            )}
            <p>
              Question {currentQuestionIndex + 1} of {totalQuestions}
            </p>
          </div>
        </div>

        <div>
          <div className="timer-section">
            <h1 className="header">Timer</h1>
            <div className="timer-container">
              <div className="timer-circle">
                <svg width="120" height="120">
                  <circle
                    r={radius}
                    cx="60"
                    cy="60"
                    fill="none"
                    stroke="#e7e7e7"
                    strokeWidth="8"
                  ></circle>
                  <circle
                    r={radius}
                    cx="60"
                    cy="60"
                    fill="none"
                    stroke="green"
                    strokeWidth="8"
                    strokeDasharray={circumference}
                    style={{
                      strokeDashoffset:
                        ((totalTime - timeLeft) / totalTime) * circumference,
                    }}
                  ></circle>
                </svg>
                <div className="timer-text">{timeLeft} s</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Quiz;
